    private void btnGuessActionPerformed(java.awt.event.ActionEvent evt) {// GEN-FIRST:event_btnGuessActionPerformed
        int geniesAnswer = (int) (1 + (Math.random() * (6 - 1)));
        int guess = Integer.parseInt(spnGuess.getValue().toString());
        boolean acertou = (guess == geniesAnswer);
        String fala = acertou ? "<html><p style='color: blue'>ACERTOU!</p></html>"
                : "ERROU! Pensei em ".concat(Integer.toString(geniesAnswer));
        txtGeniesAnswer.setText(fala);
    }// GEN-LAST:event_btnGuessActionPerformed

